    class Solution {
        /**
         * æ€è·¯ï¼šç±»ä¼¼ä¸Šä¸€é¢˜ï¼Œ(i,j) å¤„çš„æœ€å¤§è·¯å¾„ç­‰äºä¸Šé¢å’Œå·¦è¾¹çš„è·¯å¾„å’Œ
         * å³ dp[i][j] = dp[i-1][j] + dp[i][j-1];
         */
        public int uniquePathsWithObstacles1(int[][] obstacleGrid) {
            if (obstacleGrid == null || obstacleGrid.length == 0 || obstacleGrid[0].length == 0) {
                return 0;
            }

            int row = obstacleGrid.length, column = obstacleGrid[0].length;
            int[] dp = new int[column + 1];
            dp[0] = obstacleGrid[0][0] == 1 ? 0 : 1;
            for (int i = 0; i < row; i++) {
                for (int j = 0; j < column; j++) {
                    if (obstacleGrid[i][j] == 1) {
                        // æ­¤è·¯ä¸é€šï¼Œä¹‹å‰çš„è·¯å¾„å½’0
                        dp[j] = 0;
                        continue;
                    }
                    // æ»šåŠ¨æ•°ç»„è§£æ³•ï¼Œä¸‹ä¸€è¡Œç›´æ¥ç»§æ‰¿ä¸Šä¸€è¡Œçš„å€¼
                    // æ‰€ä»¥çœç•¥äº†å½“j=0æ—¶çš„èµ‹å€¼æ“ä½œ
                    if (j - 1 >= 0 && obstacleGrid[i][j] == 0) {
                        dp[j] += dp[j - 1];
                    }
                }
            }

            return dp[column - 1];
        }

        public int uniquePathsWithObstacles(int[][] obstacleGrid) {
            if (obstacleGrid == null || obstacleGrid.length == 0 || obstacleGrid[0].length == 0) {
                return 0;
            }

            int row = obstacleGrid.length, column = obstacleGrid[0].length;
            int[][] dp = new int[row][column];

            for (int j = 0; j < column && obstacleGrid[0][j] == 0; j++) {
                dp[0][j] = 1;
            }
            for (int i = 0; i < row && obstacleGrid[i][0] == 0; i++) {
                dp[i][0] = 1;
            }

            for (int i = 1; i < row; i++) {
                for (int j = 1; j < column; j++) {
                    if (obstacleGrid[i][j] == 0) {
                        dp[i][j] = dp[i - 1][j] + dp[i][j - 1];
                    }
                }
            }

            return dp[row - 1][column - 1];
        }
    }
